/*Table structure for table `activity` */

DROP TABLE IF EXISTS `activity`;

CREATE TABLE `activity` (
  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT '课程报名id 主键',
  `ACTIVITYENAME` varchar(255) DEFAULT NULL COMMENT '课程名',
  `ACTIVITYSTATUS` varchar(255) DEFAULT NULL COMMENT '课程状态',
  `CREATETIME` varchar(255) DEFAULT NULL COMMENT '创建时间',
  `STARTTIME` varchar(255) DEFAULT NULL COMMENT '开始时间',
  `ENDTIME` varchar(255) DEFAULT NULL COMMENT '结束时间',
  `CONTENT` text COMMENT '内容',
  `CREATEUSER` varchar(255) DEFAULT NULL COMMENT '创建人',
  `TEACHER` varchar(255) DEFAULT NULL COMMENT '讲师',
  `LOCATION` varchar(255) DEFAULT NULL COMMENT '地址',
  `RANDOMCODE` varchar(255) DEFAULT NULL COMMENT '随机码',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8;

