/*Table structure for table `activity_join` */

DROP TABLE IF EXISTS `activity_join`;

CREATE TABLE `activity_join` (
  `ID` int(11) NOT NULL AUTO_INCREMENT,
  `ACT_ID` int(11) DEFAULT NULL,
  `USERNAME` varchar(255) DEFAULT NULL COMMENT '用户名',
  `PHONE` varchar(255) DEFAULT NULL COMMENT '电话',
  `JOINTIME` varchar(255) DEFAULT NULL COMMENT '上课时间',
  `EMAIL` varchar(255) DEFAULT NULL COMMENT '电子邮件',
  `COMPANY` varchar(255) DEFAULT NULL COMMENT '申请单位',
  `STATUS` varchar(255) DEFAULT NULL COMMENT '人物状态',
  `NUMBER` varchar(255) DEFAULT NULL COMMENT '报名人数',
  `RANDOMCODE` varchar(255) DEFAULT NULL COMMENT '随机码',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=82 DEFAULT CHARSET=utf8;

