  `STATUS` varchar(255) DEFAULT NULL COMMENT '状态',
  `ARTICLE_URL` varchar(255) DEFAULT NULL COMMENT '图片链接',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8;

